mapper.select:
select userId,name,status,time 
from record 


        name like '%${name}%'



        and time between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}



limit #{start,jdbcType=INTEGER},#{offset,jdbcType=INTEGER}

select count(*) from record


        name like '%${name}%'



        and time between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
